        public int count = 0;
        public int UpDown(string input)
        {
            var chars = input.ToCharArray();
            int result = 0;

            for (int i = 0; i < chars.Length; i++)
            {
                if (chars[i] == '(') result++;
                if (chars[i] == ')') result--;
            }
            return result;
        }

        public int Basement(string input)
        {
            var chars = input.ToCharArray();
            int result = 0;
            int pos = 1;
            for (int i = 0; i < chars.Length; i++)
            {
                if (chars[i] == '(') result++;
                if (chars[i] == ')') result--;
                if (result == -1) { pos = i + 1; break; }
            }
            return pos;
        }
