        public int HouseVisit(string input)
        {
            var chars = input.ToCharArray();
            var houseList = new List<string>();
            var houseListPresents = new List<string>();
            int xx = 0;
            int yy = 0;
            int presents = 1;
            int numberofhouses = 1;
            Program p = new Program();

            houseList.Add(coord(xx, yy));
            houseListPresents.Add(coordandpresents(xx, yy, presents));
            for (int i = 0; i < chars.Length; i++)
            {
                if (chars[i] == '>')
                {
                    ++xx;
                }
                if (chars[i] == '<')
                {
                    --xx;
                }
                if (chars[i] == '^')
                {
                    ++yy;
                }
                if (chars[i] == 'v')
                {
                    --yy;
                }
                if (vistedBefore(coord(xx, yy), houseList))
                {
                    //increase presents by one
                }
                else
                {
                    houseList.Add(coord(xx, yy));
                    houseListPresents.Add(coordandpresents(xx, yy, 1));
                    ++numberofhouses;
                }
            }
            return numberofhouses;

        }
        public int HouseVisit2(string input)
        {
            var chars = input.ToCharArray();
            var houseList = new List<string>();
            var houseListPresents = new List<string>();
            int xx = 0;
            int yy = 0;
            int presents = 2;
            int numberofhouses = 1;
            Program p = new Program();

            houseList.Add(coord(xx, yy));
            houseListPresents.Add(coordandpresents(xx, yy, presents));
            for (int i = 0; i < chars.Length; i += 2)
            {
                if (chars[i] == '>')
                {
                    ++xx;
                }
                if (chars[i] == '<')
                {
                    --xx;
                }
                if (chars[i] == '^')
                {
                    ++yy;
                }
                if (chars[i] == 'v')
                {
                    --yy;
                }
                if (vistedBefore(coord(xx, yy), houseList))
                {
                    //increase presents by one
                }
                else
                {
                    houseList.Add(coord(xx, yy));
                    houseListPresents.Add(coordandpresents(xx, yy, 1));
                    ++numberofhouses;
                }
            }
            xx = 0;//reset coords for second santa
            yy = 0;
            for (int i = 1; i < chars.Length; i += 2)
            {
                if (chars[i] == '>')
                {
                    ++xx;
                }
                if (chars[i] == '<')
                {
                    --xx;
                }
                if (chars[i] == '^')
                {
                    ++yy;
                }
                if (chars[i] == 'v')
                {
                    --yy;
                }
                if (vistedBefore(coord(xx, yy), houseList))
                {
                    //increase presents by one
                }
                else
                {
                    houseList.Add(coord(xx, yy));
                    houseListPresents.Add(coordandpresents(xx, yy, 1));
                    ++numberofhouses;
                }
            }
            return numberofhouses;

        }
        public string coordandpresents(int x, int y, int presents)
        {
            return x.ToString() + "," + y.ToString() + "/" + presents.ToString();
        }
        public string coord(int x, int y)
        {
            return x.ToString() + "," + y.ToString();
        }
        public bool vistedBefore(string coord, List<string> houseList)
        {
            return houseList.Contains(coord);
        }
